package com.liang.media.adapter;import android.database.Cursor;import android.net.Uri;import android.support.annotation.NonNull;import android.support.v7.widget.RecyclerView;import android.view.LayoutInflater;import android.view.View;import android.view.ViewGroup;import android.widget.ImageView;import android.widget.TextView;import com.liang.media.R;import com.liang.media.RequestConfig;import com.liang.media.bean.MediaFolder;import java.io.File;public class FolderAdapter extends RecyclerViewCursorAdapter<FolderAdapter.FolderHolder> {    private OnItemClickListener mOnItemClickListener;    public FolderAdapter() {        super(null);    }    public void setOnItemClickListener(OnItemClickListener onItemClickListener) {        mOnItemClickListener = onItemClickListener;    }    @NonNull    @Override    public FolderHolder onCreateViewHolder(@NonNull ViewGroup viewGroup, int viewType) {        View view = LayoutInflater.from(viewGroup.getContext()).inflate(R.layout.media_album_item, viewGroup, false);        return new FolderHolder(view);    }    @Override    protected void onBindViewHolder(FolderHolder viewHolder, Cursor cursor) {        final MediaFolder mediaFolder = new MediaFolder(cursor);        viewHolder.folder_title.setText(mediaFolder.name);        viewHolder.folder_count.setText("（" + mediaFolder.count + "）");        Uri uri = Uri.fromFile(new File(mediaFolder.path));        RequestConfig.getInstance().imageLoader                .loadFolderImage(viewHolder.itemView.getContext(), uri, viewHolder.folder_icon);        viewHolder.itemView.setOnClickListener(new View.OnClickListener() {            @Override            public void onClick(View v) {                if (mOnItemClickListener != null) {                    mOnItemClickListener.onItemClick(mediaFolder);                }            }        });    }    @Override    protected int getItemViewType(int position, Cursor cursor) {        return 0;    }    public class FolderHolder extends RecyclerView.ViewHolder {        private ImageView folder_icon;        private TextView folder_title;        private TextView folder_count;        FolderHolder(View itemView) {            super(itemView);            folder_icon = itemView.findViewById(R.id.folder_icon);            folder_title = itemView.findViewById(R.id.folder_title);            folder_count = itemView.findViewById(R.id.folder_count);        }    }    public interface OnItemClickListener {        void onItemClick(MediaFolder mediaFolder);    }}